import logo from './logo.svg';
import './App.css';
import Confetti from 'react-confetti'
import useWindowSize from './useWindowSize';
import Countdown from 'react-countdown-simple';


function App() {
  const { width, height } = useWindowSize()

  const renderer = ({ days, hours, minutes, seconds }) =>
  <div>{days}/{hours}/{minutes}/{seconds}</div>

  const oneHour = new Date(
    '13-10-22'
  ).toISOString()

  return (
   <div className='m_container' style={{width:width, height:height}}>
        <div style={{width:width, height:height, zIndex:1}}>
          <Confetti
            width={width}
            height={height}
            gravity={0.03}
          />
        </div>
        <div style={{width:width, height:height, zIndex:2, justifyContent:'center', alignItems:'center', padding:20, }}>
          <div className='counter'>
            <Countdown targetDate={oneHour} renderer={renderer}/>
          </div>
        </div>

   </div>
  );
}

export default App;
