import logo from './logo.svg';
import './App.css';
import Confetti from 'react-confetti'
import useWindowSize from './useWindowSize';
import Countdown from 'react-countdown-simple';


function App() {
  const { width, height } = useWindowSize()

  const renderer = ({ days, hours, minutes, seconds }) =>
  <h3 className='counter'>{`${days} day ${hours} hours ${minutes} minutes ${seconds} seconds `}</h3>

  const oneHour = new Date('15 October 2022 19:00 UTC').toISOString();

  return (
   <div className='m_container' style={{width:width, height:height}}>
        <div style={{width:width, height:height, zIndex:1, position:'absolute', opacity:0.5}}>
          <Confetti
            width={width}
            height={height}
            gravity={0.03}
          />
        </div>
        <div style={{width:width, height:height, zIndex:2, justifyContent:'center', alignItems:'center',  display:'flex'}}>
          <div className='counter'>
            <h1 >Elenuki's day</h1>
            <Countdown targetDate={oneHour} renderer={renderer} />
            <h4 >Until Elenukis birtay in su ksita</h4>
            <div style={{fontsize:10}}>si no sabes su dirección preguntasela por motivos de security no pondremos esa información</div>
          </div>
        </div>

   </div>
  );
}

export default App;
